import React, { useEffect} from "react";
import { CREATE_BLOG_MUTATION } from "../GraphQL/Mutations";
import { useMutation } from "@apollo/client";
import{useNavigate} from "react-router-dom"
import { useForm } from "react-hook-form";







function Form(refetch) {

  const { register, handleSubmit,  formState: { errors }} = useForm();

  const [createBlog, { data: successResponse,  error }] = useMutation(CREATE_BLOG_MUTATION);

  const navigate=useNavigate();

  const toSubmit=()=>{
    navigate("/",{state:{ heading:"", content:" "}})
  }
  const toCancel=()=>{
    navigate('/',{state:{heading:"", content:" "}})
  }

  const onSubmit = (value) =>{
  addBlog(value)
  };





  const addBlog = (value) => {
    createBlog({
      variables: {
        data: {
          heading:value.heading,
          date:new Date().toLocaleString(),
          content:value.content


        }
      },
    });
  };

  useEffect(() => {
    if (successResponse?.createTodo?.id) {
      refetch();

      console.log('A new Blog is added successfully!!!');
    } else if (error) {
      console.log('Error: ' + error);
    }
  }, [successResponse, error, refetch]);


  return (
    <div  >
      <form onSubmit={handleSubmit(onSubmit)}>
      <label>Create a Blog:</label> <br/>
      <div class="p-3 mb-2 bg-secondary text-white" >
        <input  className="border border-success"
          type="text"
          placeholder="Heading"
          class="form-control"

          {...register("heading", { required: true })}
          name="heading" 

        />
        <br/>
         {errors.heading && <span>This field is required</span>}
         <br/>

        <br/>

        <input
          type="text" 
          className="form-control"
          placeholder="Content"


          {...register("content", { required: true })}
          name="content"

        /><br/>
        {errors.content && <span>This field is required</span>}
        <br/>
        <br/>



        <button onClick={toSubmit} className="btn btn-success"  type="submit">Submit</button>




      </div>
      </form>
      {/* <button onClick={toCancel}className="btn btn-danger"  type="submit">Cancel</button> */}
      <div className="gap-2 d-md-flex justify-content-md-end">

<button onClick={toCancel} type="button" className="btn btn-primary me-md-2">Cancel</button>

</div>
      <br/>


      <hr/>

    </div>
  );
}

export default Form;
