import React, { useState, useEffect } from "react";
import Carousel from 'react-bootstrap/Carousel';

import { LOAD_BLOGES } from "../GraphQL/Queries";
import { useQuery,useMutation } from "@apollo/client";
import { DELETE_BLOG_MUTATION } from "../GraphQL/Mutations";
import{useNavigate,useLocation} from "react-router-dom"

import Blog from "../images/Blog.jpg"
import img1 from "../images/img1.jpg"
import img2 from "../images/img2.jpg"

function GetBloges() {
  const { data, refetch } = useQuery(LOAD_BLOGES);
  const [bloges, setBloges] = useState([]);
  const [deleteBlog, { data: successResponse,  error }] = useMutation(DELETE_BLOG_MUTATION);

  const navigate = useNavigate();
  const location=useLocation()

if(location?.state?.refetch){
  refetch();
}

  const toCreate=()=>{
    navigate("/form",{state:{heading:" ",content:" " ,update:false}})
  }
  const toUpdate=(val)=>{
    console.log(val)
    navigate("/form",{state:{heading:val.heading,content:val.content,id:val.id,update:true}})
  }

  const deleteBlogAction = (id) => {
    deleteBlog({
      variables: {
        id: id,
      },
    });

  };
  useEffect(() => {
    if (successResponse?.deleteBlog?.id) {
      refetch();
      console.log('Blog is deleted successfully!!!');
    } else if (error) {
      console.log('Error: ' + error);
    }
  }, [successResponse, error, refetch]);

  useEffect(() => {
    if (data) {
      setBloges(data.getAllBloges);
    }
  }, [data]);

  return (
    <div>

     <div  id="header">
      <header> 

       <h1 > BLOG APP </h1> 
      </header>
     </div>

     <Carousel>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=First slide&bg=373940"
          alt="First slide"
        />
        <Carousel.Caption>
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Second slide&bg=282c34"
          alt="Second slide"
        />

        <Carousel.Caption>
          <h3>Second slide label</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Third slide&bg=20232a"
          alt="Third slide"
        />

        <Carousel.Caption>
          <h3>Third slide label</h3>
          <p>
            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>




      <div className="list-collection">
        {bloges.map((val) => {
          return (
            <div key={val.id} className="list-items">
              <div >
                <br/>
                <h4> {val.heading}</h4>
                <h4> {val.date}</h4>
                <h4> {val.content}</h4>
                <button onClick={()=>toUpdate(val)} className="btn btn-warning" > update</button> 
                {" "}
              <button className="btn btn-info" onClick={() => deleteBlogAction(val.id)}> Delete</button>
                <br/>
              </div>
            </div>
          );
        })}
      </div>
      <br/> 
      <div className="gap-2 d-md-flex justify-content-md-end"  >
          <button   className="btn btn-success me-md-2" onClick={toCreate}>CREATE BLOG</button>
          </div>

    </div>
  );
}

export default GetBloges;
