import React, { useEffect, useState } from "react";
import { CREATE_TODO_MUTATION } from "../GraphQL/Mutations";
import { useMutation } from "@apollo/client";
import './index.css';
import { useForm } from "react-hook-form";
import { Routes, Route, Link } from "react-router-dom";
import GetTodos from "./GetTodos";

function Form({refetch}) {
  const [heading, setHeading] = useState('');
  const [date, setDate] = useState('');
  const [content, setContent] = useState('');
  const { register, handleSubmit,  formState: { errors } } = useForm();

  const [createTodo, { data: successResponse, loading, error }] = useMutation(CREATE_TODO_MUTATION);
  const onSubmit = data => console.log(data);

  const addTodo = () => {
    createTodo({
      variables: {
        data: {
          heading: heading,
          date:date,
          content:content


        }
      },
    });
  };

  useEffect(() => {
    if (successResponse?.createTodo?.id) {
      refetch();
      setHeading('');
      console.log('A new todo is added successfully!!!');
    } else if (error) {
      console.log('Error: ' + error);
    }
  }, [successResponse, error, refetch]);

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
      <label>Create a Blog:</label>
      <div className="todo-form">
        <input
          type="text"
          placeholder="heading"
          name="heading"
          {...register("heading", { required: true })}

          value={heading}
          onChange={(e) => {
            setHeading(e.target.value);
          }}
        />
        <br/>
         {errors.heading && <span>This field is required</span>}
         <br/>
         <input
          type="text"
          placeholder="date"
          name="date"
          {...register("date", { required: true })}

          value={date}
          onChange={(e) => {
            setDate(e.target.value);
          }}
        /><br/>
        {errors.date && <span>This field is required</span>}
        <br/>

        <input
          type="text"
          placeholder="content"
          name="content"
          {...register("content", { required: true })}

          value={content}
          onChange={(e) => {
            setContent(e.target.value);
          }}
        /><br/>
        {errors.content && <span>This field is required</span>}
        <br/>
        <button className="create-btn" onClick={addTodo}>  Create </button>
        {loading && <div class="loader"></div>}
        {/* <Routes>
          <Route path="/GetTodos" element={<GetTodos/>}/>
        </Routes>
        <Link to="/GetTodos"> create</Link> */}


      </div>
      </form>

      <hr/>
    </div>
  );
}

export default Form;
