import React, { useEffect, useState } from "react";
import { CREATE_BLOG_MUTATION } from "../GraphQL/Mutations";
import { useMutation } from "@apollo/client";

import { useForm } from "react-hook-form";
import{Routes,Route,Link} from "react-router-dom"

import Blog from "./Blog";



function Form({refetch}) {
  // const [heading, setHeading] = useState('');
  // const [date, setDate] = useState('');
  // const [content, setContent] = useState('');
  const { register, handleSubmit,  formState: { errors }} = useForm();

  const [createBlog, { data: successResponse,  error }] = useMutation(CREATE_BLOG_MUTATION);
  const onSubmit = (data) =>{
 addBlog(data)
// console.log(data)
  } 



  const addBlog = (data) => {
    createBlog({
      variables: {
        data: {
          heading:data.heading,
          date:new Date().toLocaleString(),
          content:data.content


        }
      },
    });
  };

  useEffect(() => {
    if (successResponse?.createTodo?.id) {
      refetch();
      setHeading('');
      setDate('');
      setContent('')
      console.log('A new Blog is added successfully!!!');
    } else if (error) {
      console.log('Error: ' + error);
    }
  }, [successResponse, error, refetch]);


  return (
    <div  >
      <form onSubmit={handleSubmit(onSubmit)}>
      <label>Create a Blog:</label> <br/>
      <div class="p-3 mb-2 bg-secondary text-white" >
        <input  className="border border-success"
          type="text"
          placeholder="Heading"
          class="form-control"

          {...register("heading", { required: true })}
          name="heading" 
          value={heading}
          onChange={(e) => { 
            setHeading(e.target.value);
          }}
        />
        <br/>
         {errors.heading && <span>This field is required</span>}
         <br/>

        <br/>

        <input
          type="text" 
          className="form-control"
          placeholder="Content"


          {...register("content", { required: true })}
          name="content"
          value={content}
          onChange={(e) => {
            setContent(e.target.value);
          }}
        /><br/>
        {errors.content && <span>This field is required</span>}
        <br/>
        <br/>
        <button className="btn btn-success" type="submit"> Submit</button>



      </div>
      </form>
     <br/>
      <button   > <Link to="/Blog"> Create </Link></button>
      <br/>
      <br/>
      <Routes>
        <Route path="/Blog" element={<Blog/>}/>
      </Routes>

      <hr/>

    </div>
  );
}

export default Form;
