import React from "react";
import { useLocation } from "react-router-dom";

const More = () => {
  const location = useLocation();

  return (
    <div>
      <div id="more">
        <div>
          <h1>{location.state.heading}</h1>
        </div>

        <div>
          <h3>{location.state.date}</h3>
        </div>

        <div>
          <h3>{location.state.content}</h3>
        </div>
      </div>

      <div className="gap-2 d-md-flex justify-content-md-end View">
                          <button type="button" className="btn btn-success me-md-2">View More</button>
                      </div>
    </div>
    
  );
};

export default More;
