import React, { useEffect, useState } from "react";
import { CREATE_TODO_MUTATION } from "../GraphQL/Mutations";
import { useMutation } from "@apollo/client";
import './index.css';
import { useForm } from "react-hook-form";



function Form({refetch}) {
  const [heading, setHeading] = useState('');
  const [date, setDate] = useState('');
  const [content, setContent] = useState('');
  const { register, handleSubmit,  formState: { errors }} = useForm();

  const [createTodo, { data: successResponse,  error }] = useMutation(CREATE_TODO_MUTATION);
  const onSubmit = (data) => console.log(data);


  const addTodo = () => {
    createTodo({
      variables: {
        data: {
          heading: heading,
          date:date,
          content:content


        }
      },
    });
  };

  useEffect(() => {
    if (successResponse?.createTodo?.id) {
      refetch();
      setHeading('');
      setDate('');
      setContent('')
      console.log('A new todo is added successfully!!!');
    } else if (error) {
      console.log('Error: ' + error);
    }
  }, [successResponse, error, refetch]);

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
      <label>Create a Blog:</label>
      <div className="todo-form">
        <input
          type="text"
          placeholder="heading"

          {...register("heading", { required: true })}
          name="heading"
          value={heading}
          onChange={(e) => {
            setHeading(e.target.value);
          }}
        />
        <br/>
         {errors.heading && <span>This field is required</span>}
         <br/>
         <input
          type="text"
          placeholder="date"

          {...register("date", { required: true })}
          name="date"
          value={date}
          onChange={(e) => {
            setDate(e.target.value);
          }}
        /><br/>
        {errors.date && <span>This field is required</span>}
        <br/>

        <input
          type="text"
          placeholder="content"


          {...register("content", { required: true })}
          name="content"
          value={content}
          onChange={(e) => {
            setContent(e.target.value);
          }}
        /><br/>
        {errors.content && <span>This field is required</span>}
        <br/>

        <button type="submit"> Submit</button>



      </div>
      </form>
      <button onClick={addTodo}>  Create </button>
      <hr/>
    </div>
  );
}

export default Form;
