import React,{ useEffect} from "react";
import { CREATE_BLOG_MUTATION,UPDATE_BLOG_MUTATION } from "../GraphQL/Mutations";
import { useMutation } from "@apollo/client";
import{useNavigate,useLocation} from "react-router-dom"
import { useForm } from "react-hook-form";









function Form() {



  const { register, handleSubmit,  formState: { errors }} = useForm();

  const [createBlog, { data: successResponse,  error }] = useMutation(CREATE_BLOG_MUTATION);
  const [updateBlog, { data: updatesuccessResponse,  updateerror }] = useMutation(UPDATE_BLOG_MUTATION);
  const navigate=useNavigate();
const location=useLocation();

  const toCancel=()=>{
    navigate('/',{state:{heading:"", content:" "}})
  }

  const onSubmit = (value) =>{
  addBlog(value)

  };





  const addBlog = (value) => {
    createBlog({
      variables: {
        data: {
          heading:value.heading,
          date:new Date().toLocaleString(),
          content:value.content


        }
      },
    });
  };

 const updateBlogAction=(value,id)=>{
  updateBlog({
    variables:{
      data:{
        id:id,
        heading:value.heading,
        date:new Date().toLocaleString(),
        content:value.content,

      }
    },
  });
 };


  useEffect(() => {
    if (successResponse?.createBlog?.id) {
      navigate("/",{state:{refetch:true}});
      console.log('A new Blog is added successfully!!!');
    } else if (error) {
      console.log('Error: ' + error);
    }
  }, [successResponse, error,navigate]);


  return (
    <div  >
      <h1>Create a Blog:</h1> <br/>
      <form onSubmit={handleSubmit(onSubmit)}>

      <div class="p-3 mb-2 bg-secondary text-white" >
        <lable>Heading</lable><br/><br/>
        <input  className="border border-success"
          type="text"
          placeholder="Heading"
          class="form-control"
          defaultValue={location.state.heading}
          {...register("heading", { required: true })}
          name="heading" 

        />
        <br/>
         {errors.heading && <span>This field is required</span>}
         <br/>

        <br/>
        <lable>Content</lable><br/><br/>
        <input
          type="text" 
          className="form-control"
          placeholder="Content"
          defaultValue={location.state.content}

          {...register("content", { required: true })}
          name="content"

        /><br/>
        {errors.content && <span>This field is required</span>}
        <br/>
        <br/>



        <button className="btn btn-success"  type="submit">Submit</button>




      </div>
      </form>
      <button onClick={()=>{updateBlogAction(value.id)}}> Updated Blog</button>

      <div className="gap-2 d-md-flex justify-content-md-end">

<button onClick={toCancel} type="button" className="btn btn-danger me-md-2">Cancel</button>

</div>
      <br/>


      <hr/>

    </div>
  );
}

export default Form;
