import React, { useState, useEffect } from "react";

import { LOAD_BLOGES } from "../GraphQL/Queries";
import { useQuery, useMutation } from "@apollo/client";
import { DELETE_BLOG_MUTATION } from "../GraphQL/Mutations";
import { useNavigate, useLocation } from "react-router-dom";


function GetBloges() {
  const { data, refetch } = useQuery(LOAD_BLOGES);
  const [bloges, setBloges] = useState([]);
  const [deleteBlog, { data: successResponse, error }] =
    useMutation(DELETE_BLOG_MUTATION);

  const navigate = useNavigate();
  const location = useLocation();

  if (location?.state?.refetch) {
    refetch();
  }

  const toCreate = () => {
    navigate("/form", { state: { heading: " ", content: " ", update: false } });
  };

  const toMore=(val)=>{
    navigate("/More",{state:{
      heading:val.heading,
      content:val.content,
      id:val.id,
       date:new Date().toLocaleString()
    }
  })
  }


  const toUpdate = (val) => {
    console.log(val);
    navigate("/form", {
      state: {
        heading: val.heading,
        content: val.content,
        id: val.id,
        update: true,
      },
    });
  };

  const deleteBlogAction = (id) => {
    deleteBlog({
      variables: {
        id: id,
      },
    });
  };
  useEffect(() => {
    if (successResponse?.deleteBlog?.id) {
      refetch();
      console.log("Blog is deleted successfully!!!");
    } else if (error) {
      console.log("Error: " + error);
    }
  }, [successResponse, error, refetch]);

  useEffect(() => {
    if (data) {
      setBloges(data.getAllBloges);
    }
  }, [data]);

  return (
    <div className="container mt-5">


      <header id="header">
        <h1> BLOG APP </h1>
      </header>

      <div >
        {bloges.map((val) => {
          return (
            <div key={val.id} className="list-items">


                <h1> {val.heading} </h1>


                    <h4> {val.date}</h4>
                    <h4> {val.content}</h4>
                    <footer>
                     <div className="gap-2 d-md-flex justify-content-md-end">
                          <button onClick={()=>toMore(val)} type="button" className="btn btn-success me-md-2">View More</button>
                      </div>


                    <button
                      onClick={() => toUpdate(val)}
                      className="btn btn-warning"
                    >
                      {" "}
                      update
                    </button>{" "}
                    <button
                      className="btn btn-info"
                      onClick={() => deleteBlogAction(val.id)}
                    >
                      {" "}
                      Delete
                    </button>
                    <i class="fa-solid fa-trash"></i>
                    </footer>



              <br />
            </div>
          );
        })}
      </div>
      <button onClick={toCreate} type="button" class="btn btn-primary button">
        CREATE BLOG
      </button>
      <br />
    </div>
  );
}

export default GetBloges;
